mapper.select:
SELECT A.AUC_OBJ_DSC /* 경매대상구분 */ , A.AUC_PRG_SQ /* 경매진행순서 */
, CASE WHEN #{ss_security} = '1' THEN C.FTSNM WHEN LENGTH(C.FTSNM) = 2 THEN SUBSTR(C.FTSNM,1,1) || '*' WHEN LENGTH(C.FTSNM) = 3 THEN SUBSTR(C.FTSNM,1,1) || '*' || SUBSTR(C.FTSNM,3,1) WHEN LENGTH(C.FTSNM) = 4 THEN SUBSTR(C.FTSNM,1,1) || '**' || SUBSTR(C.FTSNM,4,1) WHEN LENGTH(C.FTSNM) >= 5 THEN SUBSTR(C.FTSNM,1,2) || '**' || SUBSTR(C.FTSNM,5,1) END AS FTSNM /*농가명*/
, SUBSTR(A.SRA_INDV_AMNNO, 1, 3) || '-' || SUBSTR(A.SRA_INDV_AMNNO, 4, 3) || '-' || SUBSTR(A.SRA_INDV_AMNNO, 7, 9) AS SRA_INDV_AMNNO /* 축산개체관리번호(바코드, 귀표번호) */ , SUBSTR(B.BIRTH, 1, 4) || '-' || SUBSTR(B.BIRTH, 5, 2) || '-' || SUBSTR(B.BIRTH, 7, 2) AS BIRTH /* 생년월일 */ , B.INDV_SEX_C /* 개체성별코드 */ , B.MCOW_DSC /* 어미구분코드 */ , RTRIM(B.KPN_NO) KPN_NO /* PKN번호 */ , B.SRA_INDV_PASG_QCN /* 계대 */ , B.MATIME /* 산차 */ , A.COW_SOG_WT /* 우출하중량 */ , A.LOWS_SBID_LMT_AM /* 최저낙찰한도금액 */ , (case when A.AUC_OBJ_DSC = '1' and A.SEL_STS_DSC = '22' then A.SRA_SBID_AM when A.AUC_OBJ_DSC = '2' and A.SEL_STS_DSC = '22' then A.SRA_SBID_UPR when A.AUC_OBJ_DSC = '3' and A.SEL_STS_DSC = '22' then A.SRA_SBID_AM else 0 end) as SRA_SBID_AM /* 축산낙찰금액 */ , (case when A.AUC_OBJ_DSC = '1' and A.SEL_STS_DSC = '22' then A.SRA_SBID_AM when A.AUC_OBJ_DSC = '2' and A.SEL_STS_DSC = '22' then A.SRA_SBID_UPR when A.AUC_OBJ_DSC = '3' and A.SEL_STS_DSC = '22' then A.SRA_SBID_AM else 0 end) - A.LOWS_SBID_LMT_AM as DIF_SBID_AM /* 낙찰가차액 */ , CASE WHEN #{ss_security} = '1' THEN D.SRA_MWMNNM WHEN LENGTH(D.SRA_MWMNNM) = 2 THEN SUBSTR(D.SRA_MWMNNM,1,1) || '*' WHEN LENGTH(D.SRA_MWMNNM) = 3 THEN SUBSTR(D.SRA_MWMNNM,1,1) || '*' || SUBSTR(D.SRA_MWMNNM,3,1) WHEN LENGTH(D.SRA_MWMNNM) = 4 THEN SUBSTR(D.SRA_MWMNNM,1,1) || '**' || SUBSTR(D.SRA_MWMNNM,4,1) WHEN LENGTH(D.SRA_MWMNNM) >= 5 THEN SUBSTR(D.SRA_MWMNNM,1,2) || '**' || SUBSTR(D.SRA_MWMNNM,5,1) END AS SRA_MWMNNM , A.SEL_STS_DSC /* 판매산태 */ FROM TB_LA_IS_MH_SOG_COW A , TB_LA_IS_MM_INDV B , TB_LA_IS_MM_FHS C , TB_LA_IS_MM_MWMN D WHERE A.NA_BZPLC = #{ss_na_bzplc} AND A.AUC_OBJ_DSC = CASE WHEN #{cb_auc_obj_dsc} = '0' THEN A.AUC_OBJ_DSC ELSE #{cb_auc_obj_dsc} END AND A.AUC_DT = #{auc_dt} AND A.LED_SQNO = 1 AND A.DEL_YN = '0' AND A.NA_BZPLC = B.NA_BZPLC AND A.SRA_INDV_AMNNO = B.SRA_INDV_AMNNO AND A.SRA_SRS_DSC = B.SRA_SRS_DSC AND A.NA_BZPLC = C.NA_BZPLC AND A.FHS_ID_NO = C.FHS_ID_NO AND A.FARM_AMNNO = C.FARM_AMNNO AND A.NA_BZPLC = D.NA_BZPLC(+) AND A.TRMN_AMNNO = D.TRMN_AMNNO(+) ORDER BY A.AUC_PRG_SQ;
