SELECT DECODE(GROUPING(A.AUC_OBJ_DSC), 1, '일계' ,substr(A.AUC_DT,1, 4) || '-' || substr(A.AUC_DT,5, 2) || '-' || substr(A.AUC_DT,7, 2)) AS AUC_DT , A.AUC_OBJ_DSC AS AUC_OBJ_DSC , A.RG_DSC AS RG_DSC , A.INDV_SEX_C AS INDV_SEX_C , ROUND(SUM(A.TOT_HDCN3) ) AS TOT_HDCN3 , ROUND(SUM(A.TOT_HDCN) ) AS TOT_HDCN , ROUND(SUM(A.TOT_HDCN2) ) AS TOT_HDCN2 , ROUND(SUM(A.TOT_WT) ) AS TOT_WT , ROUND(SUM(A.TOT_WT)/SUM(A.TOT_HDCN)) AS AVG_WT , ROUND(SUM(A.LOWS_SBID_LMT_AM) ) AS LOWS_SBID_LMT_AM , ROUND(SUM(A.LOWS_SBID_LMT_AM)/SUM(A.TOT_HDCN) ) AS AVG_PR , ROUND(SUM(A.SRA_SBID_AM) ) AS SRA_SBID_AM , ROUND(SUM(A.SRA_SBID_AM) /SUM(A.TOT_HDCN)) AS SRA_AVG_SBID_UPR , ROUND(SUM(A.SRA_SBID_AM) /SUM(A.TOT_HDCN) - SUM(A.LOWS_SBID_LMT_AM)/SUM(A.TOT_HDCN)) AS BIGO , ROUND(MAX(A.MAX_SRA_SBID_AM) ) AS MAX_SRA_SBID_AM , ROUND(MIN(A.MIN_SRA_SBID_AM) ) AS MIN_SRA_SBID_AM FROM ( SELECT A.AUC_DT AS AUC_DT , A.AUC_OBJ_DSC AS AUC_OBJ_DSC , A.RG_DSC AS RG_DSC , A.INDV_SEX_C AS INDV_SEX_C , NVL(A.TOT_HDCN, 0) + NVL(B.TOT_HDCN, 0) AS TOT_HDCN3 , NVL(A.TOT_HDCN, 0) AS TOT_HDCN , NVL(B.TOT_HDCN, 0) AS TOT_HDCN2 , NVL(A.TOT_WT, 0) AS TOT_WT , NVL(ROUND(A.AVG_WT), 0) AS AVG_WT , NVL(A.LOWS_SBID_LMT_AM, 0) AS LOWS_SBID_LMT_AM , NVL(ROUND(A.AVG_PR),0) AS AVG_PR , NVL(A.SRA_SBID_AM, 0) AS SRA_SBID_AM , NVL(ROUND(A.SRA_AVG_SBID_UPR), 0) AS SRA_AVG_SBID_UPR , NVL(ROUND(A.SRA_AVG_SBID_UPR-A.AVG_PR), 0) AS BIGO , NVL(A.MAX_SRA_SBID_AM, 0) AS MAX_SRA_SBID_AM , NVL(A.MIN_SRA_SBID_AM, 0) AS MIN_SRA_SBID_AM FROM( SELECT CASE WHEN B.RG_DSC != '09' THEN '등 록' else '없 음' END AS RG_DSC /* 등록구분 */ , A.AUC_DT AS AUC_DT /* 경매일자 */ , A.AUC_OBJ_DSC AS AUC_OBJ_DSC /* 경매대상구분코드 */ , CASE WHEN B.INDV_SEX_C IN ('1', '4', '6') THEN '1' ELSE '2' END AS INDV_SEX_C /* 성별 */ , COUNT(A.SRA_INDV_AMNNO) AS TOT_HDCN /* 두수 */ , SUM(A.COW_SOG_WT) AS TOT_WT /* 우출하 총 중량 */ , AVG(A.COW_SOG_WT) AS AVG_WT /* 우출하 평균 중량 */ , SUM(A.LOWS_SBID_LMT_AM) AS LOWS_SBID_LMT_AM /* 응찰하한가 총계 */ , AVG(A.LOWS_SBID_LMT_AM) AS AVG_PR /* 응찰하한가 평균 */
