} = '8808990659695' ) THEN MIN(A.SRA_SBID_AM) ELSE MIN(A.SRA_SBID_UPR) END) ELSE MIN(A.SRA_SBID_AM) END AS MIN_SRA_SBID_AM /* 축산최저가 */ FROM TB_LA_IS_MH_SOG_COW A , TB_LA_IS_MM_INDV B WHERE A.NA_BZPLC = #{ss_na_bzplc} AND A.AUC_OBJ_DSC = CASE WHEN #{cb_auc_obj_dsc} = '0' THEN A.AUC_OBJ_DSC ELSE #{cb_auc_obj_dsc} END AND A.AUC_DT BETWEEN #{auc_st_dt} AND #{auc_ed_dt} AND A.DEL_YN = '0' AND A.LED_SQNO = 1 AND A.SEL_STS_DSC = '22' /* 낙찰건 */ AND A.NA_BZPLC = B.NA_BZPLC AND A.SRA_SRS_DSC = B.SRA_SRS_DSC /* 축산축종구분코드 */ AND A.SRA_INDV_AMNNO = B.SRA_INDV_AMNNO /* 축산개체관리번호 */ GROUP BY CASE WHEN B.RG_DSC != '09' THEN '등 록' ELSE '없 음' END, CASE WHEN B.INDV_SEX_C IN ('1', '4', '6') THEN '1' ELSE '2' END, A.AUC_DT, A.AUC_OBJ_DSC ) A ,( SELECT CASE WHEN B.RG_DSC != '09' THEN '등 록' else '없 음' END AS RG_DSC /* 등록구분 */ , A.AUC_DT AS AUC_DT /* 경매일자 */ , A.AUC_OBJ_DSC AS AUC_OBJ_DSC /* 경매대상구분코드 */ , CASE WHEN B.INDV_SEX_C IN ('1', '4', '6') THEN '1' ELSE '2' END AS INDV_SEX_C /* 성별 */ , COUNT(A.SRA_INDV_AMNNO) AS TOT_HDCN /* 두수 */ , SUM(A.COW_SOG_WT) AS TOT_WT /* 우출하 총 중량 */ , AVG(A.COW_SOG_WT) AS AVG_WT /* 우출하 평균 중량 */ , SUM(A.LOWS_SBID_LMT_AM) AS LOWS_SBID_LMT_AM /* 응찰하한가 총계 */ , AVG(A.LOWS_SBID_LMT_AM) AS AVG_PR /* 응찰하한가 평균 */
