mapper.select:

         SELECT NVL(MAX(QCN), 0) + 1  AS MAXCNT
		   FROM TB_LA_IS_MH_AUC_QCN
          WHERE NA_BZPLC         = #{ss_na_bzplc}
          	AND AUC_OBJ_DSC      = #{hd_auc_obj_dsc}


SELECT
   COUNT(*) AS CNT
  FROM  TB_LA_IS_MH_AUC_QCN
 WHERE NA_BZPLC         = #{ss_na_bzplc}
 	AND AUC_OBJ_DSC      = #{hd_auc_obj_dsc}    
   AND AUC_DT           = #{auc_dt}
   AND DEL_YN           = '0'


  SELECT COUNT(*) AS CNT
FROM TB_LA_IS_MH_SOG_COW
WHERE NA_BZPLC               = #{ss_na_bzplc}


  AND AUC_OBJ_DSC            = #{hd_auc_obj_dsc}


  AND AUC_DT                 = #{auc_dt}
  AND DEL_YN                 = '0'

mapper.update:

         UPDATE TB_LA_IS_MH_AUC_QCN SET
	          DDL_YN           = '1'
	        , LSCHG_DTM        = SYSDATE
	        , LS_CMENO         = #{ss_userid}
	    WHERE NA_BZPLC         = #{ss_na_bzplc}
	      AND AUC_OBJ_DSC      = #{hd_auc_obj_dsc}
	      AND AUC_DT           = #{auc_dt}


         UPDATE TB_LA_IS_MH_AUC_QCN SET
	          DDL_YN           = '0'
	        , LSCHG_DTM        = SYSDATE
	        , LS_CMENO         = #{ss_userid}
	    WHERE NA_BZPLC         = #{ss_na_bzplc}
	      AND AUC_OBJ_DSC      = #{hd_auc_obj_dsc}
	      AND AUC_DT           = #{auc_dt}
