mapper.select:
SELECT
  COUNT(*) AS CNT
  FROM  TB_LA_IS_MH_AUC_ENTR
WHERE NA_BZPLC         = #{ss_na_bzplc}
  AND AUC_DT           = #{auc_date}
  AND TRMN_AMNNO       = #{trmn_amnno}
  
  AND (
    (#{hd_auc_obj_dsc} IN ( '0', '1', '2', '3' ) AND AUC_OBJ_DSC IN ( '0', '1', '2', '3' ))
    OR
    (#{hd_auc_obj_dsc} NOT IN ( '0', '1', '2', '3' ) AND AUC_OBJ_DSC = #{hd_auc_obj_dsc})
  )

SELECT
  COUNT(*) AS CNT
  FROM  TB_LA_IS_MH_AUC_ENTR
WHERE NA_BZPLC             = #{ss_na_bzplc}
  AND AUC_DT               = #{auc_date}
  AND LVST_AUC_PTC_MN_NO   = #{lvst_auc_ptc_mn_no}
  
  AND (
    (#{hd_auc_obj_dsc} IN ( '0', '1', '2', '3' ) AND AUC_OBJ_DSC IN ( '0', '1', '2', '3' ))
    OR
    (#{hd_auc_obj_dsc} NOT IN ( '0', '1', '2', '3' ) AND AUC_OBJ_DSC = #{hd_auc_obj_dsc})
  )

SELECT
  COUNT(*) AS CNT
  FROM  TB_LA_IS_MH_SOG_COW
WHERE NA_BZPLC     = #{ss_na_bzplc}
  AND AUC_DT       = #{auc_date}
  AND TRMN_AMNNO   = #{trmn_amnno}
  AND SRA_SRS_DSC  = #{sra_srs_dsc}
