         SELECT TR_PMSS_YN
		  FROM  TB_LA_IS_MH_BAD_TRMN
		  WHERE SRA_MWMNNM = #{sra_mwmnnm}
			AND FRLNO      = #{frlno}
			AND RG_DT = (SELECT MAX(RG_DT)
			   			   FROM TB_LA_IS_MH_BAD_TRMN
						  WHERE SRA_MWMNNM = #{sra_mwmnnm}
						    AND FRLNO      = #{frlno})
		    AND RG_SQNO = (SELECT MAX(RG_SQNO)
							 FROM TB_LA_IS_MH_BAD_TRMN
							WHERE SRA_MWMNNM = #{sra_mwmnnm}
							  AND FRLNO      = #{frlno}
							  AND RG_DT = (SELECT MAX(RG_DT)
											 FROM TB_LA_IS_MH_BAD_TRMN
											WHERE SRA_MWMNNM = #{sra_mwmnnm}
											  AND FRLNO      = #{frlno}))


SELECT
   COUNT(*) AS CNT
  FROM  TB_LA_IS_MH_AUC_ENTR
 WHERE NA_BZPLC         = #{ss_na_bzplc}
 	AND AUC_OBJ_DSC      = #{hd_auc_obj_dsc}          	
   AND AUC_DT           = #{auc_date}
   AND TRMN_AMNNO       = #{trmn_amnno}


SELECT
   COUNT(*) AS CNT
  FROM  TB_LA_IS_MH_AUC_ENTR
 WHERE NA_BZPLC             = #{ss_na_bzplc}
 	AND AUC_OBJ_DSC          = #{hd_auc_obj_dsc}          	
   AND AUC_DT               = #{auc_date}
   AND LVST_AUC_PTC_MN_NO   = #{lvst_auc_ptc_mn_no}


SELECT
   COUNT(*) AS CNT
  FROM  tb_la_is_mh_sog_cow
 WHERE NA_BZPLC     = #{ss_na_bzplc}
 	AND AUC_OBJ_DSC  =  CASE WHEN #{hd_auc_obj_dsc} = '0' THEN AUC_OBJ_DSC ELSE #{hd_auc_obj_dsc} END
   AND AUC_DT       = #{auc_date}
   AND TRMN_AMNNO   = #{trmn_amnno}
