   SET RMK_CNTN       = ''
 WHERE NA_BZPLC      = #{ss_na_bzplc} 
   AND AUC_DT        = #{auc_dt}
   AND SEL_STS_DSC  != '22'

   AND AUC_OBJ_DSC   = #{auc_obj_dsc}

mapper.delete:
