 WHERE NA_BZPLC      = #{ss_na_bzplc} 
   AND AUC_DT        = #{auc_dt}
   AND AUC_OBJ_DSC   = #{auc_obj_dsc}

DELETE FROM TB_LA_IS_MH_CALF 
 WHERE NA_BZPLC      = #{ss_na_bzplc} 
   AND AUC_DT        = #{auc_dt}
   AND AUC_OBJ_DSC   = #{auc_obj_dsc}

