     , SELFEE_CHK_YN       = #{selfee_chk_yn}
     , SELFEE_CHK_YN_FEE   = #{selfee_chk_yn_fee}
     , LSCHG_DTM           = SYSDATE
     , LS_CMENO            =  #{ss_userid}
 WHERE NA_BZPLC            = #{ss_na_bzplc}
   AND AUC_OBJ_DSC         = #{auc_obj_dsc}
   AND AUC_DT              = #{auc_dt}
   AND AUC_PRG_SQ          = #{auc_prg_sq}
   AND DEL_YN              = '0'
