SELECT T.* , E.CLNTNM , T.AUC_DT , SUBSTR(T.AUC_DT, 0,4) || '년 ' || SUBSTR(T.AUC_DT, 5,2) || '월 ' || SUBSTR(T.AUC_DT, 7,2) || '일' AS AUC_DT_STR , NVL(F.TMS_YN, 'N') AS TMS_YN , NVL(F.SMS_FWDG_DT, '') AS SMS_FWDG_DT , G.TELNO CLNT_TELNO , G.BRC AS ADJ_BRC , C.SIMP_CNM AS BANK_NM , G.ACNO , NVL(D.CUS_MPNO,'') AS CUS_MPNO , NVL(TRIM(D.FTSNM),'') AS REVE_USR_NM , T.TOT_SRA_SBID_AM - T.TOT_SRA_TR_FEE AS TOT_AM , T.AUC_DT || LPAD(T.RNUM, 4, '0') || NVL(#{sms_key},0) SMS_SEND_KEY FROM ( SELECT ROW_NUMBER() OVER(ORDER BY A.NA_BZPLC,A.AUC_DT,A.FHS_ID_NO,A.FARM_AMNNO
, A.AUC_OBJ_DSC
) AS RNUM , A.NA_BZPLC , ${auc_obj_dsc} AUC_OBJ_DSC , A.AUC_DT , A.FHS_ID_NO , A.FARM_AMNNO , COUNT(*) AS TOT_CNT , COUNT(DECODE(A.SEL_STS_DSC,'22',1)) AS TOT_CNT_NAK , COUNT(DECODE(A.SEL_STS_DSC,'22',NULL,1)) AS TOT_CNT_YOU , SUM(NVL(A.SRA_SBID_AM,0)) AS TOT_SRA_SBID_AM , SUM(NVL(C.SRA_TR_FEE,0)) AS TOT_SRA_TR_FEE FROM TB_LA_IS_MH_SOG_COW A INNER JOIN TB_LA_IS_MM_INDV B ON A.NA_BZPLC = B.NA_BZPLC AND A.SRA_INDV_AMNNO = B.SRA_INDV_AMNNO AND A.SRA_SRS_DSC = B.SRA_SRS_DSC LEFT JOIN ( SELECT SUM(NVL(SRA_TR_FEE, 0)* DECODE(ANS_DSC, 1, 1, -1) ) AS SRA_TR_FEE , NA_BZPLC , AUC_OBJ_DSC , AUC_DT , OSLP_NO , LED_SQNO , FEE_APL_OBJ_C FROM TB_LA_IS_MH_FEE_IMPS WHERE FEE_APL_OBJ_C = '1' AND AUC_DT = #{auc_dt} AND NA_BZPLC = #{ss_na_bzplc} AND AUC_OBJ_DSC = DECODE(#{auc_obj_dsc},'0',AUC_OBJ_DSC,#{auc_obj_dsc}) GROUP BY NA_BZPLC, AUC_OBJ_DSC, AUC_DT, OSLP_NO, LED_SQNO, FEE_APL_OBJ_C ) C ON A.NA_BZPLC = C.NA_BZPLC AND A.AUC_OBJ_DSC = C.AUC_OBJ_DSC AND A.AUC_DT = C.AUC_DT AND A.OSLP_NO = C.OSLP_NO AND A.LED_SQNO = C.LED_SQNO WHERE 1=1 AND A.NA_BZPLC = #{ss_na_bzplc} AND A.AUC_DT = #{auc_dt} AND A.AUC_OBJ_DSC = DECODE(#{auc_obj_dsc},'0',A.AUC_OBJ_DSC,#{auc_obj_dsc})
GROUP BY A.NA_BZPLC, A.AUC_DT, A.FHS_ID_NO, A.FARM_AMNNO
, A.AUC_OBJ_DSC
) T LEFT JOIN TB_LA_IS_MM_FHS D ON T.NA_BZPLC = D.NA_BZPLC AND T.FHS_ID_NO = D.FHS_ID_NO AND T.FARM_AMNNO = D.FARM_AMNNO INNER JOIN TB_LA_IS_BM_BZLOC E ON T.NA_BZPLC = E.NA_BZPLC INNER JOIN TB_LA_IS_MM_WMC G ON T.NA_BZPLC = G.NA_BZPLC LEFT JOIN TB_LA_IS_MM_SMS F ON T.NA_BZPLC = F.NA_BZPLC AND T.AUC_OBJ_DSC = F.AUC_OBJ_DSC AND F.TRMN_AMNNO = T.AUC_DT || LPAD(T.RNUM, 4, '0') || NVL(#{sms_key},0) AND F.DPAMN_DSC = '2' AND TMS_TYPE = '02' LEFT JOIN TB_LA_IS_MH_COMN_APL C ON C.SIMP_C = G.REP_BNK_C AND C.SIMP_TPC = 'BNK_C' AND C.SIMP_C_GRP_SQNO = '1' WHERE LENGTH(REPLACE(NVL(D.CUS_MPNO,''), '-', '')) >= 10 AND LENGTH(REGEXP_REPLACE(NVL(D.CUS_MPNO,''), '[^0-9]')) >= 10

SELECT T.* , DECODE( CASE WHEN T.AUC_OBJ_DSC = '1' THEN ROUND(NVL(J.CALF_AUC_ATDR_UNT_AM, 10000)) WHEN T.AUC_OBJ_DSC = '2' THEN ROUND(NVL(J.NBFCT_AUC_ATDR_UNT_AM, 1)) WHEN T.AUC_OBJ_DSC = '3' THEN ROUND(NVL(J.PPGCOW_AUC_ATDR_UNT_AM, 10000)) WHEN T.AUC_OBJ_DSC = '5' THEN ROUND(NVL(J.GT_AUC_ATDR_UNT_AM, 10000)) WHEN T.AUC_OBJ_DSC = '6' THEN ROUND(NVL(J.HS_AUC_ATDR_UNT_AM, 10000)) ELSE 10000 END,10000, '만원', 1000, '천원', '원') AS UNIT , C.SIMP_CNM AS AUC_OBJ_DSC_NAME FROM ( SELECT A.NA_BZPLC ,A.AUC_DT ,#{auc_obj_dsc} AS AUC_OBJ_DSC ,COUNT(*) TOT_CNT ,COUNT(CASE WHEN B.INDV_SEX_C IN ('1','4','6') THEN 1 END) SEX_W_CNT ,COUNT(CASE WHEN B.INDV_SEX_C NOT IN ('1','4','6') THEN 1 END) SEX_M_CNT ,NVL(MAX(DECODE(A.SEL_STS_DSC,'22',A.SRA_SBID_AM)),0) MAX_SRA_SBID_AM ,NVL(MIN(DECODE(A.SEL_STS_DSC,'22',A.SRA_SBID_AM)),0) MIN_SRA_SBID_AM ,NVL(ROUND(AVG(DECODE(A.SEL_STS_DSC,'22',A.SRA_SBID_AM))),0) AVG_SRA_SBID_AM ,NVL(MAX(CASE WHEN B.INDV_SEX_C IN ('1','4','6') THEN DECODE(A.SEL_STS_DSC,'22',A.SRA_SBID_UPR) END),0) MAX_SRA_SBID_AM_W ,NVL(MIN(CASE WHEN B.INDV_SEX_C IN ('1','4','6') THEN DECODE(A.SEL_STS_DSC,'22',A.SRA_SBID_UPR) END),0) MIN_SRA_SBID_AM_W ,NVL(ROUND(AVG(CASE WHEN B.INDV_SEX_C IN ('1','4','6') THEN DECODE(A.SEL_STS_DSC,'22',A.SRA_SBID_UPR) END)),0) AVG_SRA_SBID_AM_W ,NVL(MAX(CASE WHEN B.INDV_SEX_C NOT IN ('1','4','6') THEN DECODE(A.SEL_STS_DSC,'22',A.SRA_SBID_UPR) END),0) MAX_SRA_SBID_AM_M ,NVL(MIN(CASE WHEN B.INDV_SEX_C NOT IN ('1','4','6') THEN DECODE(A.SEL_STS_DSC,'22',A.SRA_SBID_UPR) END),0) MIN_SRA_SBID_AM_M ,NVL(ROUND(AVG(CASE WHEN B.INDV_SEX_C NOT IN ('1','4','6') THEN DECODE(A.SEL_STS_DSC,'22',A.SRA_SBID_UPR) END)),0) AVG_SRA_SBID_AM_M FROM TB_LA_IS_MH_SOG_COW A INNER JOIN TB_LA_IS_MM_INDV B ON A.NA_BZPLC = B.NA_BZPLC AND A.SRA_INDV_AMNNO = B.SRA_INDV_AMNNO AND A.SRA_SRS_DSC = B.SRA_SRS_DSC WHERE A.NA_BZPLC = #{ss_na_bzplc}
AND A.AUC_DT = #{auc_dt} GROUP BY A.NA_BZPLC,A.AUC_DT
AND A.AUC_OBJ_DSC = #{auc_obj_dsc} AND A.AUC_DT = #{auc_dt} GROUP BY A.NA_BZPLC,A.AUC_DT,A.AUC_OBJ_DSC
) T LEFT OUTER JOIN TB_LA_IS_MM_ENV_EST J ON ( T.NA_BZPLC = J.NA_BZPLC ) LEFT OUTER JOIN ( SELECT SIMP_C, SIMP_CNM FROM TB_LA_IS_MH_COMN_APL WHERE SIMP_TPC = 'AUC_OBJ_DSC' AND SIMP_C_GRP_SQNO = '7' ) C ON ( C.SIMP_C = T.AUC_OBJ_DSC ) ORDER BY T.NA_BZPLC,T.AUC_DT,T.AUC_OBJ_DSC
