mapper.insert:
select 'G' || (nvl(max(SUBSTRING(fhs_id_no, 2)), 100000) + 1) as sra_fhs_id_no from tb_la_is_mm_fhs where fhs_id_no like 'G%' and na_bzplc = #{ss_na_bzplc}
select #{sra_fhs_id_no} as sra_fhs_id_no from dual
insert into tb_la_is_mm_fhs( na_bzplc , fhs_id_no , farm_amnno , farm_id_no , na_trpl_c , ftsnm , zip , dongup , dongbw , ohse_telno , cus_mpno , maco_yn , rmk_cntn , jrdwo_dsc , del_yn , anw_yn , hdwk_rg_yn , sra_farm_acno , new_sra_farm_acno , fsrg_dtm , fsrgmn_eno , lschg_dtm , ls_cmeno , SRA_FED_SPY_YN , birth , mb_intg_no ) values ( #{ss_na_bzplc} , trim(#{sra_fhs_id_no}) , trim(#{farm_amnno}) , trim(#{farm_id_no}) , trim('') , trim(#{ftsnm}) , trim(#{zip}) , #{dongup} , #{dongbw} , trim(#{ohse_telno}) , trim(#{cus_mpno}) , trim(#{maco_yn}) , #{rmk_cntn} , trim(#{jrdwo_dsc}) , trim('0') , trim('9') , trim('1') , trim(#{io_sra_farm_acno}) , trim(#{io_sra_farm_acno}) , SYSDATE , #{ss_userid} , SYSDATE , #{ss_userid} , trim(#{sra_fed_spy_yn}) , trim(#{birth}) , #{mb_intg_no} )

mapper.update:
update tb_la_is_mm_fhs set
      farm_id_no        = trim(#{farm_id_no})
    , ftsnm             = trim(#{ftsnm})
    , zip               = trim(#{zip})
    , dongup            = #{dongup}
    , dongbw            = #{dongbw}
    , ohse_telno        = trim(#{ohse_telno})
    , cus_mpno          = trim(#{cus_mpno})
    , maco_yn           = trim(#{maco_yn})
    , rmk_cntn          = #{rmk_cntn}
    , jrdwo_dsc         = trim(#{jrdwo_dsc})
    , sra_farm_acno     = trim(#{io_sra_farm_acno})
    , new_sra_farm_acno = trim(#{io_sra_farm_acno})
    , lschg_dtm         = SYSDATE
    , ls_cmeno          = #{ss_userid}
    , SRA_FED_SPY_YN    = #{sra_fed_spy_yn}
    , birth             = #{birth}
    , del_yn            = #{del_yn}
    , mb_intg_no        = #{mb_intg_no}
where na_bzplc          = #{ss_na_bzplc}
  and trim(fhs_id_no)   = trim(#{sra_fhs_id_no})
  and trim(farm_amnno)  = trim(#{farm_amnno})
