   , #{indv_id_no}
   , #{sra_indv_brdsra_rg_no}
   , #{rg_dsc}
   , '9'
   , #{grfa_sra_kpn_no}
   , #{grfa_sra_indv_eart_no}
   , #{grmo_sra_indv_eart_no}
   , #{mtgrfa_sra_kpn_no}
