mapper.select:
SELECT A.NA_BZPLC , A.BLBD_DSC , A.BBRD_SQNO , A.RL_SQNO , A.BBRD_TINM , B.USRNM , TO_CHAR(A.FSRG_DTM, 'YYYY-MM-DD HH24:MI:SS') AS FSRG_DTM , A.BBRD_INQ_CN , A.FIX_YN FROM TB_LA_IS_MM_BLBD A LEFT OUTER JOIN TB_LA_IS_MM_USR B ON A.FSRGMN_ENO = B.USRID WHERE 1=1 AND A.NA_BZPLC = #{na_bzplc}
AND A.BLBD_DSC in ('2','3')
AND A.BLBD_DSC = #{blbd_dsc}
AND A.RL_SQNO = 0 AND A.BBRD_DEL_YN != '1' AND (NVL(#{cb_condi}, '0') = '1' AND A.BBRD_TINM LIKE '%'||#{bbrd_tinm_cntn}||'%' OR NVL(#{cb_condi}, '0') = '2' AND A.BBRD_CNTN LIKE '%'||#{bbrd_tinm_cntn}||'%' OR NVL(#{cb_condi}, '0') = '3' AND B.USRNM LIKE '%'||#{bbrd_tinm_cntn}||'%') ORDER BY FIX_YN desc, FSRG_DTM desc

mapper.insert:
INSERT INTO TB_LA_IS_MM_APDFL(
    APDFL_ID
  , RG_SQNO
  , FLNM
  , XCRNM
  , FL_SZE
  , FL_CNTN
  , DEL_YN
  , FSRG_DTM
  , FSRGMN_ENO
  , LSCHG_DTM
  , LS_CMENO    
)VALUES(
    #{apdfl_id}
  , (SELECT NVL(MAX(RG_SQNO),0) + 1
       FROM TB_LA_IS_MM_APDFL
      WHERE APDFL_ID = #{apdfl_id}
    )
  , #{flnm}
  , #{xcrnm}
  , #{fl_sze}
  , #{fl_cntn}
  , '0'
  , SYSDATE
  , #{ss_userid}
  , SYSDATE
  , #{ss_userid}
)

mapper.delete:
UPDATE TB_LA_IS_MM_APDFL
   SET DEL_YN    = '1'
     , LSCHG_DTM = SYSDATE
     , LS_CMENO  = #{ss_userid} 
 WHERE APDFL_ID = #{apdfl_id}
   AND RG_SQNO  = 1
