mapper.select:
SELECT A.NA_BZPLC /* 경제통합사업장코드 */ , A.AUC_OBJ_DSC /* 경매대상구분코드 */ , A.AUC_DT /* 경매일자 */ , A.OSLP_NO /* 원표번호 */ , A.LED_SQNO /* 원장일련번호 */ , SUBSTR(A.SRA_INDV_AMNNO, 1, 3) || '-' || SUBSTR(A.SRA_INDV_AMNNO, 4, 3) || '-' || SUBSTR(A.SRA_INDV_AMNNO, 7, 9) AS SRA_INDV_AMNNO /* 축산개체관리번호 */ , SUBSTR(SRA_INDV_AMNNO, -5, 4) AS SRA_INDV_AMNNO1 /* 귀표번호(대표코드) */ , B.FTSNM /* 출하주명 */ , A.RMK_CNTN /* 비고내용 */ , A.COW_SOG_WT /* 우출하중량 */ , A.FIR_LOWS_SBID_LMT_AM /* 최초최저낙찰한도금액 */ , A.AUC_PRG_SQ /* 경매번호 */ , A.MODL_NO /* 거치대 번호 */ , A.LOWS_SBID_LMT_AM / (SELECT CASE WHEN #{cb_auc_obj_dsc} = '1' THEN CALF_AUC_ATDR_UNT_AM WHEN #{cb_auc_obj_dsc} = '2' THEN NBFCT_AUC_ATDR_UNT_AM WHEN #{cb_auc_obj_dsc} = '3' THEN PPGCOW_AUC_ATDR_UNT_AM WHEN #{cb_auc_obj_dsc} = '5' THEN GT_AUC_ATDR_UNT_AM WHEN #{cb_auc_obj_dsc} = '6' THEN HS_AUC_ATDR_UNT_AM ELSE 10000 END FROM TB_LA_IS_MM_ENV_EST WHERE NA_BZPLC = #{ss_na_bzplc}) AS LOWS_SBID_LMT_AM /* 최저낙찰한도금액 */ FROM TB_LA_IS_MH_SOG_COW A , TB_LA_IS_MM_FHS B WHERE A.NA_BZPLC = #{ss_na_bzplc} AND A.AUC_DT = #{auc_dt}
AND ( (#{cb_auc_obj_dsc} = '0' AND A.AUC_OBJ_DSC IN ('0', '1', '2', '3')) OR (#{cb_auc_obj_dsc} != '0' AND A.AUC_OBJ_DSC = #{cb_auc_obj_dsc}) ) AND A.NA_BZPLC = B.NA_BZPLC AND A.FHS_ID_NO = B.FHS_ID_NO AND A.FARM_AMNNO = B.FARM_AMNNO AND A.DEL_YN = '0' AND A.SEL_STS_DSC = '11'
AND B.FHS_ID_NO = #{fhs_id_no}
ORDER BY A.AUC_PRG_SQ

mapper.update:
         UPDATE TB_LA_IS_MH_SOG_COW SET
                    MODL_NO = #{modl_no}
					, LSCHG_DTM              = SYSDATE
				    , LS_CMENO               = #{ss_userid}
                WHERE NA_BZPLC				= #{ss_na_bzplc}
                  AND AUC_DT				= #{auc_dt}
                  AND AUC_OBJ_DSC   		= #{auc_obj_dsc}
                  AND OSLP_NO			    = #{oslp_no}
                  AND LED_SQNO              = '1'
                  AND DEL_YN				= '0'

UPDATE TB_LA_IS_MH_SOG_COW SET
		RMK_CNTN = #{rmk_cntn}
		, LSCHG_DTM              = SYSDATE
		, LS_CMENO               = #{ss_userid}
	WHERE NA_BZPLC				= #{ss_na_bzplc}
		AND AUC_DT				= #{auc_dt}
		AND AUC_OBJ_DSC   		= #{auc_obj_dsc}
		AND OSLP_NO			    = #{oslp_no}
		AND LED_SQNO              = '1'
		AND SEL_STS_DSC			= '11'
		AND DEL_YN				= '0'
