mapper.insert:
INSERT INTO TB_LA_IS_MM_PGM
    ( pgid
    , pgmnm
    , flnm
    , pgm_dsc
    , uyn
    , fsrg_dtm
    , fsrgmn_eno
    , lschg_dtm
    , ls_cmeno
    ) VALUES (
        #{de_pgid}
      , #{de_pgmnm}
      , #{de_flnm}
      , #{de_pgm_dsc}
      , #{de_uyn}
      , SYSDATE
      , #{ss_userid}
      , SYSDATE
      , #{ss_userid}
    )

INSERT INTO TB_LA_IS_MM_PGM_BTN
    ( pgid
    , btn_c
    , btn_tpc
    , uyn
    , fsrg_dtm
    , fsrgmn_eno
    , lschg_dtm
    , ls_cmeno
    ) VALUES (
        #{pgid}
      , #{btn_c}
      , #{btn_tpc}
      , '1'
      , SYSDATE
      , #{ss_userid}
      , SYSDATE
      , #{ss_userid}
    )

mapper.update:
         UPDATE TB_LA_IS_MM_PGM
		    SET PGMNM     = #{de_pgmnm}
		      , UYN       = #{de_uyn}
		      , FLNM      = #{de_flnm}
		      , LSCHG_DTM = SYSDATE
		      , LS_CMENO  = #{ss_userid}
		  WHERE PGID  = #{de_pgid}

        UPDATE TB_LA_IS_MM_PGM_BTN
		   SET UYN       = #{btn_uyn}
		     , LSCHG_DTM = SYSDATE
		     , LS_CMENO  = #{ss_userid}
		 WHERE PGID  = #{de_pgid}
		   AND BTN_C = #{btn_c}
