mapper.delete:
DELETE FROM TB_LA_IS_MH_FEE_IMPS
 WHERE NA_BZPLC    = #{na_bzplc}
   AND AUC_DT      = #{auc_dt}
   AND AUC_OBJ_DSC = #{auc_obj_dsc}
   AND OSLP_NO     = #{oslp_no}

mapper.select:
SELECT A.NA_BZPLC /* 경제통합사업장코드 */ , A.APL_DT /* 적용일자 */ , A.AUC_OBJ_DSC /* 경매대상구분코드 */ , A.FEE_RG_SQNO /* 수수료등록일련번호 */ , A.NA_FEE_C /* 경제통합수수료코드 */ , A.SRA_FEENM /* 축산수수료명 */ , A.JNLZ_BSN_DSC /* 분개업무구분코드 */ , A.SRA_NA_FEE_C /* 축산경제통합수수료코드*/ , A.FEE_APL_OBJ_C /* 수수료적용대상코드 */ , A.AM_RTO_DSC /* 금액비율구분코드 */ , A.SGNO_PRC_DSC /* 단수처리구분코드 */ , A.MACO_FEE_UPR /* 조합원수수료단가 */ , A.NMACO_FEE_UPR /* 비조합원수수료단가 */ , A.ANS_DSC /* 가감구분코드 */ , A.SBID_YN /* 낙찰여부 */ , A.PPGCOW_FEE_DSC /* 번식우수수료구분코드 */ , A.DEL_YN /* 삭제여부 */ , A.FSRG_DTM /* 최초등록일시 */ , A.FSRGMN_ENO /* 최초등록자개인번호 */ , A.LSCHG_DTM /* 최종변경일시 */ , A.LS_CMENO /* 최종변경자개인번호 */ , 0 AS SRA_TR_FEE /* 축산거래수수료 */ FROM TB_LA_IS_MH_FEE A WHERE A.NA_BZPLC = #{na_bzplc} /* 경제통합사업장코드 */
AND A.AUC_OBJ_DSC = #{auc_obj_dsc}
AND A.APL_DT = (SELECT MAX(APL_DT) FROM TB_LA_IS_MH_FEE WHERE NA_BZPLC = A.NA_BZPLC /* 경제통합사업장코드 */ AND AUC_OBJ_DSC = A.AUC_OBJ_DSC /* 경매대상구분코드 */ AND APL_DT <= #{auc_dt} /* 적용일자 */ AND DEL_YN = '0') /* 적용일자 */ AND A.DEL_YN = '0' /* 삭제여부 */

mapper.insert:


			SELECT NVL(MAX(SRA_OPR_SQNO)+1,1) AS SRA_OPR_SQNO
