, NULL
, #{birth}
/* 생년월일 */ , #{mcow_dsc} /* 어미구분코드 */ , #{kpn_no} /* KPN번호 */ , #{indv_sex_c} /* 개체성별코드 */ , #{mcow_sra_indv_amnno} /* 어미소축산개체관리번호 */
, NULL
