mapper.update:

UPDATE TB_LA_IS_MM_USR SET
       PW        = Damo.ENC_B64('KEY1', #{io_new_pw})
     , PWERR_NT  = 0
     , STRG_DT   = TO_CHAR(SYSDATE,'YYYYMMDD')
     , LSCHG_DTM = SYSDATE
     , LS_CMENO  = #{usrid}
 WHERE NA_BZPLC  = (SELECT na_bzplc FROM TB_LA_IS_MM_USR WHERE usrid = #{usrid})  
   AND usrid     = #{usrid}
   AND attc_no   = #{usr_pw}

mapper.select:

select count(*)
  from TB_LA_IS_MM_USR
 where usrid = #{user_id}

mapper.update:
UPDATE TB_LA_IS_MM_USR
   SET pwerr_nt = #{PWERR_NT} + 1
 WHERE na_bzplc = #{NA_BZPLC}
   AND usrid    = #{USRID}

UPDATE TB_LA_IS_MM_USR
   SET na_bzplc = #{na_bzplc}
 WHERE na_bzplc = #{ss_na_bzplc}
   AND usrid    = #{ss_userid}

UPDATE TB_LA_IS_MM_USR
   SET pwerr_nt = 0
 WHERE na_bzplc = #{NA_BZPLC}
   AND usrid    = #{USRID}

mapper.select:
SELECT PW
  FROM TB_LA_IS_MM_USR
 WHERE USRID = #{usrid}
